function a(o){if(!o)return"Never";const n=new Date(o),e=new Date-n,t=Math.floor(e/6e4);if(t<1)return"Just now";if(t<60)return`${t} min ago`;const r=Math.floor(t/60);return r<24?`${r}h ago`:n.toLocaleDateString()}function f(o){const n=o?.sessions??[];if(n.length===0)return null;const s=n.map(e=>e.last_used_at).filter(Boolean);return s.length===0?null:s.sort((e,t)=>new Date(t)-new Date(e))[0]}export{a as f,f as g};
